import React, { useState } from 'react';
import axios from 'axios';
import './BookListing.css';

const BookListing = () => {
  const [books, setBooks] = useState([]);
  const [search, setSearch] = useState('');
  const [author, setAuthor] = useState('');
  const [genre, setGenre] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Function to fetch books based on filters
  const fetchBooks = async (filters = {}) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('http://localhost:5002/api/books', { params: filters });
      setBooks(response.data);
    } catch (err) {
      setError('Error fetching books');
    } finally {
      setLoading(false);
    }
  };

  // Handle the search button click
  const handleSearch = () => {
    fetchBooks({ search, author, genre });
  };

  return (
    <div>
      <h1>Book Listing</h1>
      <div className="filter-bar">
        <input
          type="text"
          placeholder="Search by title"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <input
          type="text"
          placeholder="Search by author"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
        <select value={genre} onChange={(e) => setGenre(e.target.value)}>
          <option value="">Filter by genre</option>
          <option value="Fiction">Fiction</option>
          <option value="Non-Fiction">Non-Fiction</option>
          <option value="Fantasy">Fantasy</option>
          <option value="Science">Science</option>
        </select>
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Display loading and error states */}
      {loading && <div>Loading...</div>}
      {error && <div>{error}</div>}

      {/* Display search results */}
      <div className="book-list">
        {books.length > 0 ? (
          books.map((book) => (
            <div key={book._id} className="book-item">
              <h3>{book.title}</h3>
              <p><strong>Author:</strong> {book.author}</p>
              <p><strong>Genre:</strong> {book.genre}</p>
              <p>{book.description}</p>
            </div>
          ))
        ) : (
          !loading && <p>No books found. Use the search bar above to find books.</p>
        )}
      </div>
    </div>
  );
};

export default BookListing;
