import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './HomePage.css'; // Optional: For styling

const HomePage = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchCriteria, setSearchCriteria] = useState({
    title: '',
    author: '',
    genre: '',
  });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:5002/api/books');
        setBooks(response.data);
        setLoading(false);
      } catch (err) {
        setError('Error fetching books');
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  const handleInputChange = (e) => {
    setSearchCriteria({ ...searchCriteria, [e.target.name]: e.target.value });
  };

  const handleSearch = () => {
    // Navigate to the BookListing page with search criteria
    navigate('/books', { state: searchCriteria });
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Featured Books</h1>

      {/* Search Filters */}
      <div className="search-filters">
        <input
          type="text"
          name="title"
          placeholder="Search by Title"
          value={searchCriteria.title}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="author"
          placeholder="Search by Author"
          value={searchCriteria.author}
          onChange={handleInputChange}
        />
        <select name="genre" value={searchCriteria.genre} onChange={handleInputChange}>
          <option value="">Filter by Genre</option>
          <option value="Fiction">Fiction</option>
          <option value="Non-Fiction">Non-Fiction</option>
          <option value="Fantasy">Fantasy</option>
          <option value="Science">Science</option>
          <option value="Real">Real</option>
          {/* Add more genres as needed */}
        </select>
        <button onClick={handleSearch}>Browse All Books</button>
      </div>

      {/* Featured Books */}
      <div className="book-list">
        {books.length > 0 ? (
          books.map((book) => (
            <div key={book._id} className="book-item">
              <h3>{book.title}</h3>
              <p><strong>Author:</strong> {book.author}</p>
              <p><strong>Description:</strong> {book.description}</p>
            </div>
          ))
        ) : (
          <p>No books available.</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
